import three from "../Asset/three.jpg"
import Footer from "./Footer";
import Register from "./Register";
import Login from "./Login";
import { useState } from "react";
const Home=()=>{


  const [isOpen, setIsOpen] = useState(false);
  const [open, setOpen] = useState(false);

  const openPopup = () => {
    setIsOpen(true);
  };

  const openPop = () => {
    setOpen(true);
  };

  const closePopup = () => {
    setIsOpen(false);
  };

  const closePop = () => {
    setOpen(false);
  };


    return(
        <>
   <nav>
   {/* <img width="100%"  src={three}/> */}

<div>
   <div style={{marginTop:"-50px"}}>
                <button onClick={openPopup} className="sbutton"  >Sign up</button>
                {isOpen && (
                  <div className="popup-container">
                    <div className="popup-box">
                      <span className="close1" onClick={closePopup}>close</span>
                      <h2>This is a Popup</h2>
                      <Register />
                      <p>Hello! This is some sample popup content.</p>
                    </div>
                  </div>
                )}
              </div>


              
              <div>
                <button onClick={openPop} className="s1button"  to="login"  >Login</button>
                {open && (
                  <div className="popup-container">
                    <div className="popup-box">
                      <span className="close" onClick={closePop}>close</span>
                      <Login />
                    </div>
                  </div>
                )}
              </div>

              </div>
    </nav>  
   <Footer/>
        </>
    )
}

export default Home;
