import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

const Display = () => {
  const myNav = useNavigate();
  const [taskData, setTaskData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = taskData.slice(firstIndex, lastIndex);
  const npage = Math.ceil(taskData.length / recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1);

  // Load task data from the server
  const loadData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/task/Display");
      setTaskData(response.data);
    } catch (error) {
      console.error("Error fetching task data:", error);
    }
  };

  // Handle toggling task status
  const handleToggleStatus = async (id, status) => {
    try {
      const newStatus = status === 'pending' ? 'completed' : 'pending';
      const updatedTodo = await axios.put(`http://localhost:8000/task/Display/${id}`, { status: newStatus });
      setTaskData(taskData.map(todo => (todo._id === id ? updatedTodo.data : todo)));
    } catch (error) {
      console.error("Error toggling task status:", error);
    }
  };

  // Delete task
  const del = (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      axios.post("http://localhost:8000/task/Delete", { id: id })
        .then(() => {
          loadData();
        })
        .catch((error) => {
          console.error("Error deleting item:", error);
        });
    }
  };

  // Edit task
  const edit = (id) => {
    myNav("/edit/" + id);
  };

  // View task description
  const descriptionHere = (description, date, descriptiondetail) => {
    myNav(`/Des/${description}/${date}/${descriptiondetail}`);
  };

  // Load task data when component mounts
  useEffect(() => {
    loadData();
  }, []);

  // Generate table rows for task data
  const mydata = records.map((task, index) => (
    <tr key={index}>
      <td className="sr">{index + 1}</td>
      <td>{task.title}</td>
      <td onClick={() => descriptionHere(task.description, task.date, task.descriptiondetail)}>{task.description}</td>
      <td>{task.date}</td>
      <td>
        <div style={{ display: "flex" }}>
          <div className="stat">{task.status}</div>
          <label className="material-checkbox">
            <input type="checkbox" onClick={() => handleToggleStatus(task._id, task.status)} />
            <span className="checkmark"></span>
          </label>
        </div>
      </td>
      <td className="edit1">
        <button onClick={() => edit(task._id)} className="edit-button">
          
        </button>
      </td>
      <td className="edit1">
        <button onClick={() => del(task._id)} className="Btn">
          {/* Delete icon */}
        </button>
      </td>
    </tr>
  ));

  // Pagination functionality
  const prevPage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage !== npage) {
      setCurrentPage(currentPage + 1);
    }
  };

  const changeCPage = (id) => {
    setCurrentPage(id);
  };

  return (
    <>
      <div style={{ backgroundColor: "#2d2d2d" }}>
        <center><h1 style={{ color: "white", marginBottom: "40px" }}>Assigned Tasks</h1></center>
        <div className="sidebar">
          <table id="utables" className="tables">
            <thead className="tablehead">
              <tr>
                <th className="sr">Sr.</th>
                <th>Title</th>
                <th>Description</th>
                <th>Date</th>
                <th>Status</th>
                <th className="edit1">Edit</th>
                <th className="edit1">Delete</th>
              </tr>
            </thead>
            <tbody className="tbody">
              {mydata}
            </tbody>
          </table>

          <nav className="page">
            <ul className="pagination">
              <li className="page-item">
                <a href="#" className="page-link" onClick={prevPage}>Prev</a>
              </li>
              {numbers.map((n) => (
                <li key={n} className={`page-item ${currentPage === n ? 'active' : ''}`}>
                  <a href="#" className="page-link" onClick={() => changeCPage(n)}>{n}</a>
                </li>
              ))}
              <li className="page-item">
                <a href="#" className="page-link" onClick={nextPage}>Next</a>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </>
  )
}

export default Display;
