import { useState,useEffect } from "react";
import axios from "axios";
import "./priorList.css"



const Prioritylist=()=>{

    const[taskData,setTaskData]=useState([])


    const loadData=()=>{
        axios.get("http://localhost:8000/task/Display").then((res)=>{
        setTaskData(res.data)
        })
    }

    useEffect(()=>{
        loadData()
    },[])


    const handleDragStart = (e, id) => {
        e.dataTransfer.setData("id", id);
      };
    
      const handleDrop = (e, targetIndex, targetPriority) => {
        const id = e.dataTransfer.getData("id");
        const draggedItem = taskData.find(item => item._id === id);
        const updatedEmpData = taskData.filter(item => item._id !== id);
        if (draggedItem.priority !== targetPriority) {
          draggedItem.priority = targetPriority;
        }
        updatedEmpData.splice(targetIndex, 0, draggedItem);
        setTaskData(updatedEmpData);
      };
    
      const allowDrop = (e) => {
        e.preventDefault();
      };
    
      const getColorByPriority = (priority) => {
        switch (priority) {
          case 'high':
            return 'yellow';
          case 'medium':
            return 'orange';
          case 'low':
            return 'lightgreen';
          default:
            return 'transparent';
        }
      };
    






    const mydata1=taskData.map((key, index) =>{
        if(key.priority==="High"){
        return(
            <>
            <div >
                <span>{key.title}</span><span>{key.description}</span>
            </div>
            </>
        )
        }
    })
    const mydata2=taskData.map((key, index) =>{
        if(key.priority==="Medium"){
        return(
            <>
            <div  >

                <span>{key.title}</span><span>{key.description}</span>
            </div>
            </>
        )
        }
    })
    const mydata3=taskData.map((key, index) =>{
        if(key.priority==="Low"){
        return(
            <>
            <div  >
                <span>{key.title}</span><span>{key.description}</span>
            
            </div>
            </>
        )
        }
    })

    return(
        <>  
            
            <div className="priority">
                    <div className="prioritybox">
                        <h1>High Priority</h1>
                        {mydata1}
                    </div>
                    <div className="prioritybox">
                    <h1>Medium Priority</h1>
                        {mydata2}
                    </div>
                    <div className="prioritybox">
                    <h1>Low Priority</h1>
                        {mydata3}
                    </div>
            </div>
       








            <h1>Display records</h1>
      <div style={{ display: "flex" }}>
        {["high", "medium", "low"].map((priority) => (
          <div
            key={priority}
            onDrop={(e) => handleDrop(e, empData.length, priority)}
            onDragOver={(e) => allowDrop(e)}
            style={{ marginLeft: "20px", border: "1px solid black", padding: "10px" }}
          >
            <h2>{priority.charAt(0).toUpperCase() + priority.slice(1)} Priority</h2>
            {ez.map((item, index) => {
              if (item.priority === priority) {
                return (
                  <div
                    key={item._id}
                    draggable
                    onDragStart={(e) => handleDragStart(e, item._id)}
                    onDrop={(e) => handleDrop(e, index, priority)}
                    onDragOver={(e) => allowDrop(e)}
                    style={{ backgroundColor: getColorByPriority(item.priority), margin: "5px", padding: "5px" }}
                  >
                    {/* <div onClick={() => Hi(item.description, item.date, item.fulldescription)}>{item.title}</div>
                    <div>Status: {item.status}</div> */}
                    {/* <button onClick={() => handleToggleStatus(item._id, item.status)}>Toggle Status</button> */}
                  </div>
                );
              }
              return null;
            })}
          </div>
        ))}
      </div>







        </>
    )
}

export default Prioritylist;
