import { useParams,useNavigate } from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
const Edit=()=>{
    const {id}=useParams();
    const myNav=useNavigate();
    const[input, setinput]=useState({})

    const loadData=()=>{
        let url="http://localhost:8000/task/geteditdata"
        axios.post(url,{id:id}).then((res)=>{
         setinput(res.data)
         console.log(res.data)
        })
    }

    const handleInput=(e)=>{
        let name=e.target.name
        let value=e.target.value
        setinput(values=>({...values,[name]:value}))
    }

    const handleSubmit=()=>{
        
        let url="http://localhost:8000/task/editsave"
        axios.post(url,input).then((res)=>{
            alert('updated')
            myNav("/display")
        })
    }


    useEffect(()=>{
        loadData()
    },[])


    return(
        <>




                





















<center style={{backgroundColor:"#2d2d2d"}}>
<h1>Edit Task</h1>
<div >

                <div class="form-container">
      <form class="form">
        <div class="form-group">


          <label for="title">Task Name:</label>
 

          <input type="text" name="title" value={input.title} onChange={handleInput} />
        </div>

        <div class="form-group">
          <label >Enter Description Title:</label>
          

          <input type="text" class="form-control" name="description" value={input.description} onChange={handleInput} />
        </div>



        <div class="form-group">
          <label for="textarea">Enter Description:</label>
         
          <textarea rows="8"  name="descriptiondetail" value={input.descriptiondetail} onChange={handleInput} ></textarea>
         
                  </div>


        <div class="form-group">
          <label for="pwd">Due Date</label>
         

          <input type="date" name="date" value={input.date} onChange={handleInput} />
        </div>

       

        <div class="form-group" >
          <label for="priority">priority:</label>
         

          
                   
                        <select  name="priority" value={input.priority} onChange={handleInput} >
                        <option>Select Task Priority</option>
                        <option>High</option>
                        <option>Medium</option>
                        <option>Low</option>    
                        </select>
                      


        </div>
       
        

        <button class="form-submit-btn" type="submit" onClick={handleSubmit} >Assign Task</button>

      </form>
    </div>


       </div>  

       </center>       
        </>
    )
}

export default Edit;
