import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import Insert from '../Components/Insert';
import Display from '../Components/Display';
import PriorityList from '../Components/priorList';
import Manageusers from '../Components/ManageUsers';
import Logout from '../Components/Logout';
import { useNavigate } from 'react-router-dom';
import six from "../Asset/six.jpg"
import "../Components/Sidebar.css"

function SideNavbar() {
  const mynav = useNavigate();

  // State variables for managing component visibility
  const [showImage, setShowImage] = useState(true);
  const [showInsert, setShowInsert] = useState(false);
  const [showDisplay, setShowDisplay] = useState(false);
  const [showPriorityList, setShowPriorityList] = useState(false);
  const [showManageUsers, setShowManageUsers] = useState(false);
  const [showLogout, setShowLogout] = useState(false);

  // Function to handle image click
  const handleImageClick = () => {
    setShowImage(false);
  };

  // Function to handle insert click
  const handleInsertClick = () => {
    setShowInsert(true);
    setShowDisplay(false);
    setShowPriorityList(false);
    setShowManageUsers(false);
    setShowLogout(false);
  };

  // Function to handle display click
  const handleDisplayClick = () => {
    setShowDisplay(true);
    setShowInsert(false);
    setShowPriorityList(false);
    setShowManageUsers(false);
    setShowLogout(false);
  };

  // Function to handle priority list click
  const handlePriorityListClick = () => {
    setShowPriorityList(true);
    setShowInsert(false);
    setShowDisplay(false);
    setShowManageUsers(false);
    setShowLogout(false);
  };

  // Function to handle manage users click
  const handleManageUsersClick = () => {
    setShowManageUsers(true);
    setShowInsert(false);
    setShowDisplay(false);
    setShowPriorityList(false);
    setShowLogout(false);
  };

  // Function to handle logout
  const logout = () => {
    mynav("/home");
  };

  return (
    <>
      {showImage && <img src={six} width="100%" onClick={handleImageClick} />}

      <div className="side-navbar">
        <ul>
          <a className="navbar-brand" href="#">Admin Panel</a>
          <ul className="navbar-nav">

            <li className="nav-item">
              <a className="nav-link" href="#" onClick={handleInsertClick}>Insert</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={handleDisplayClick}>View</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={handlePriorityListClick}>Priority View</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#" onClick={handleManageUsersClick}>Users Information</a>
            </li>
            <li className="nav-item">
              <Link to="Home" onClick={logout}>Logout</Link>
            </li>
          </ul>
        </ul>
      </div>

      {showInsert && <Insert />}
      {showDisplay && <Display />}
      {showPriorityList && <PriorityList />}
      {showManageUsers && <Manageusers />}

    </>
  );
}

export default SideNavbar;
