import { useState, useEffect } from "react";
import axios from "axios";
import { DndProvider, useDrag, useDrop } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";
import "./priorList.css";

const Prioritylist = () => {
    const [taskData, setTaskData] = useState([]);

    const loadData = () => {
        axios.get("http://localhost:8000/task/Display").then((res) => {
            setTaskData(res.data);
        });
    };

    useEffect(() => {
        loadData();
    }, []);

    const moveTask = (dragIndex, hoverIndex, priority) => {
        const draggedTask = taskData[dragIndex];
        const updatedTaskData = [...taskData];
        updatedTaskData.splice(dragIndex, 1);
        updatedTaskData.splice(hoverIndex, 0, draggedTask);
        setTaskData(updatedTaskData);
        // You might want to send an API request to update the backend with the new order
    };

    const Task = ({ task, index, priority }) => {
        const [{ isDragging }, drag] = useDrag({
            type: "task",
            item: { index, priority },
            collect: (monitor) => ({
                isDragging: monitor.isDragging(),
            }),
        });

        const [, drop] = useDrop({
            accept: "task",
            hover(item) {
                if (item.priority !== priority) {
                    moveTask(item.index, index, priority);
                    item.index = index;
                    item.priority = priority;
                }
            },
        });

        const opacity = isDragging ? 0.4 : 1;

        return (
            <div ref={(node) => drag(drop(node))} style={{ opacity }}>
                <span>{task.title}</span>
                <span>{task.description}</span>
            </div>
        );
    };

    return (
        <DndProvider backend={HTML5Backend}>
            <div className="priority">
                <div className="prioritybox">
                    <h1>High Priority</h1>
                    {taskData.map((task, index) => {
                        if (task.priority === "High") {
                            return <Task key={index} task={task} index={index} priority="High" />;
                        }
                        return null;
                    })}
                </div>
                <div className="prioritybox">
                    <h1>Medium Priority</h1>
                    {taskData.map((task, index) => {
                        if (task.priority === "Medium") {
                            return <Task key={index} task={task} index={index} priority="Medium" />;
                        }
                        return null;
                    })}
                </div>
                <div className="prioritybox">
                    <h1>Low Priority</h1>
                    {taskData.map((task, index) => {
                        if (task.priority === "Low") {
                            return <Task key={index} task={task} index={index} priority="Low" />;
                        }
                        return null;
                    })}
                </div>
            </div>
        </DndProvider>
    );
};

export default Prioritylist;
