import { Link, Outlet, useNavigate } from "react-router-dom";
import { useState, useEffect } from 'react';
import Login from "./Components/Login";
import Register from "./Components/Register";

const Layout = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [open, setOpen] = useState(false);

  const openPopup = () => {
    setIsOpen(true);
  };

  const openPop = () => {
    setOpen(true);
  };

  const closePopup = () => {
    setIsOpen(false);
  };

  const closePop = () => {
    setOpen(false);
  };

  const [Headercontent, setHeadercontent] = useState();
  const myNav = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("_id",);
    localStorage.removeItem("name");
    localStorage.removeItem("email");
    localStorage.removeItem("mobile");
    localStorage.removeItem("address");
    localStorage.removeItem("city");
    localStorage.removeItem("gender");
    localStorage.removeItem("role");
    localStorage.removeItem("info");
    setOpen(false);
    setIsOpen(false);
    myNav("/Home");
  }

  useEffect(() => {
    if (localStorage.getItem("role") === "admin") {
      setHeadercontent(
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="Navset">
            <a className="navbar-brand" href="#">Admin Panel</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
              <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="" className="Nlinks" >Assign</Link></a>
                </li>
              <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="insert" className="Nlinks" >Assign</Link></a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="display" className="Nlinks" >View</Link></a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="Priority" className="Nlinks" >Priority View</Link></a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="manageuser" className="Nlinks" >Users Information</Link></a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="Home" onClick={handleLogout} className="Nlinks" >Logout</Link></a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      );
    } else if (localStorage.getItem("role") === "user") {
      setHeadercontent(
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark" >
          <div className="Navset">
            <h3>User Panel</h3>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                <li className="nav-item">
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="display" className="Nlinks" >Display Task</Link></a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#"><Link to="home" onClick={handleLogout} className="Nlinks" >Logout</Link></a>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      );
    } else {
      setHeadercontent(
        <div className="nbar">
          <div className="bars">
            <div className="navi">
              <h6 className="logo">TMS<br />solutions</h6>
              <div className="bar">
                <h6 className="tab">Solutions</h6>
                <h6 className="tab">Pricing</h6>
                <h6 className="tab">Task</h6>
              </div>
              <div>
                <button onClick={openPopup} className="sbutton"  >Sign up</button>
                {isOpen && (
                  <div className="popup-container">
                    <div className="popup-box">
                      <span className="close1" onClick={closePopup}>close</span>
                      <h2>This is a Popup</h2>
                      <Register />
                      <p>Hello! This is some sample popup content.</p>
                    </div>
                  </div>
                )}
              </div>
              <div>
                <button onClick={openPop} className="s1button"  to="login"  >Login</button>
                {open && (
                  <div className="popup-container">
                    <div className="popup-box">
                      <span className="close" onClick={closePop}>close</span>
                      <Login />
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      );
    }
  });

  return (
    <>
      {Headercontent}
      <Outlet />
    </>
  );
}

export default Layout;
