import { useState, useEffect } from "react";
import axios from "axios";
import "./priorList.css";

const Prioritylist = () => {
    const [taskData, setTaskData] = useState([]);

    const loadData = () => {
        axios.get("http://localhost:8000/task/Display").then((res) => {
            setTaskData(res.data);
        });
    };

    useEffect(() => {
        loadData();
    }, []);

    const getPriorityColor = (priority) => {
        switch (priority) {
            case "High":
                return "red"; // High priority color
            case "Medium":
                return "orange"; // Medium priority color
            case "Low":
                return "green"; // Low priority color
            default:
                return "black"; // Default color
        }
    };

    return (
        <div className="priority">
            <div className="prioritybox" style={{ color: getPriorityColor("High") }}>
                <h1>High Priority</h1>
                {taskData.map((task, index) => {
                    if (task.priority === "High") {
                        return (
                            <div key={index}>
                                <span>{task.title}</span>
                                <span>{task.description}</span>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>
            <div className="prioritybox" style={{ color: getPriorityColor("Medium") }}>
                <h1>Medium Priority</h1>
                {taskData.map((task, index) => {
                    if (task.priority === "Medium") {
                        return (
                            <div key={index}>
                                <span>{task.title}</span>
                                <span>{task.description}</span>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>
            <div className="prioritybox" style={{ color: getPriorityColor("Low") }}>
                <h1>Low Priority</h1>
                {taskData.map((task, index) => {
                    if (task.priority === "Low") {
                        return (
                            <div key={index}>
                                <span>{task.title}</span>
                                <span>{task.description}</span>
                            </div>
                        );
                    }
                    return null;
                })}
            </div>
        </div>
    );
};

export default Prioritylist;
