import { Link, Outlet, useNavigate } from "react-router-dom";
import { useState, useEffect } from 'react';

const Layout = () => {
  // State to hold the header content
  const [Headercontent, setHeadercontent] = useState();
  const myNav = useNavigate();

  // Function to handle logout
  const handleLogout = () => {
    // Remove user data from localStorage
    localStorage.clear();
    // Navigate to the Home page
    myNav("/Home");
  }

  useEffect(() => {
    // Check the role of the user stored in localStorage
    if (localStorage.getItem("role") === "user") {
      // If user role is 'user', render user panel header
      setHeadercontent(
        <div style={{ backgroundColor: "#2d2d2d" }}>
          <div style={{ display: "flex", paddingTop: "20px", marginTop: "0px", marginLeft: "400px", backgroundColor: "#2d2d2d" }}>
            <div className="head"><h3>User Panel</h3></div>
            <div className="nvbar2">
              <Link to="display" className="nvbar"><h4>View Task</h4></Link>
            </div>
            <div>
              <div>
                <Link to="home" className="nvbar1" onClick={handleLogout}><h4>Logout</h4></Link>
              </div>
            </div>
          </div>
        </div>
      );
    } else {
      // If user role is not 'user', render default header
      setHeadercontent(
        <div className="nbar">
          <div className="bars">
            <div className="navi">
              <h6 className="logo">TMS<br />solutions</h6>
              <div className="bar">
                <h6 className="tab">Solutions</h6>
                <h6 className="tab">Pricing</h6>
                <h6 className="tab">Task</h6>
              </div>
            </div>
          </div>
        </div>
      );
    }
  }, []); // Empty dependency array to ensure useEffect runs only once

  return (
    <>
      {Headercontent}
      <Outlet />
    </>
  );
}

export default Layout;
