import { useState,useEffect } from "react";
import axios from "axios";
import "./priorList.css"



const Prioritylist=()=>{

    const[taskData,setTaskData]=useState([])


    const loadData=()=>{
        axios.get("http://localhost:8000/task/Display").then((res)=>{
        setTaskData(res.data)
        })
    }

    useEffect(()=>{
        loadData()
    },[])


    const handleDragStart = (e, id) => {
        e.dataTransfer.setData("id", id);
      };
    
      const handleDrop = (e, targetIndex, targetPriority) => {
        const id = e.dataTransfer.getData("id");
        const draggedItem = t.find(item => item._id === id);
        const updatedEmpData = empData.filter(item => item._id !== id);
        if (draggedItem.priority !== targetPriority) {
          draggedItem.priority = targetPriority;
        }
        updatedEmpData.splice(targetIndex, 0, draggedItem);
        setEmpData(updatedEmpData);
      };
    
      const allowDrop = (e) => {
        e.preventDefault();
      };
    
      const getColorByPriority = (priority) => {
        switch (priority) {
          case 'high':
            return 'yellow';
          case 'medium':
            return 'orange';
          case 'low':
            return 'lightgreen';
          default:
            return 'transparent';
        }
      };
    






    const mydata1=taskData.map((key, index) =>{
        if(key.priority==="High"){
        return(
            <>
            <div >
                <span>{key.title}</span><span>{key.description}</span>
            </div>
            </>
        )
        }
    })
    const mydata2=taskData.map((key, index) =>{
        if(key.priority==="Medium"){
        return(
            <>
            <div  >

                <span>{key.title}</span><span>{key.description}</span>
            </div>
            </>
        )
        }
    })
    const mydata3=taskData.map((key, index) =>{
        if(key.priority==="Low"){
        return(
            <>
            <div  >
                <span>{key.title}</span><span>{key.description}</span>
            
            </div>
            </>
        )
        }
    })

    return(
        <>  
            
            <div className="priority">
                    <div className="prioritybox">
                        <h1>High Priority</h1>
                        {mydata1}
                    </div>
                    <div className="prioritybox">
                    <h1>Medium Priority</h1>
                        {mydata2}
                    </div>
                    <div className="prioritybox">
                    <h1>Low Priority</h1>
                        {mydata3}
                    </div>
            </div>
       
        </>
    )
}

export default Prioritylist;
