import { useState } from 'react';
import axios from 'axios';
import {_apiurluser } from "./APIurlpath/_apiurl";

function Register() {

  const [ output , setOutput ] = useState();
  const [ name , setName ] = useState();     
  const [ email , setEmail ] = useState();
  const [ password , setPassword ] = useState();
  const [ mobile , setMobile ] = useState();
  const [ address , setAddress ] = useState();
  const [ city , setCity ] = useState();
  const [ gender , setGender ] = useState();  




  const handleSubmit=()=>{
    var userDetails={"name":name,"email":email,"password":password,"mobile":mobile,"address":address,"city":city,"gender":gender}

    axios.post(_apiurluser+"save",userDetails).then((response)=>{
      setOutput(response.data.result);
      setName("");
      setEmail("");
      setPassword("");
      setMobile("");
      setAddress("");
      setCity("");
    }).catch((err)=>{
      console.log(err);
    })
}



  return (
    <div>

      <div >
        <div >
            <div >
              <div >
                <font style={{"color":"//#123D52"}}>{output}</font>
                <h1  >Register <span >Now</span></h1>

                    <form>
                        <div >
                          <label >Name:</label>
                          <input type="text"  value={name} onChange={e => setName(e.target.value)}  />
                        </div>

                        <div >
                          <label for="email">Email address:</label>
                          <input type="email" value={email} onChange={e => setEmail(e.target.value)} />
                        </div>

                        <div >
                          <label for="pwd">Password:</label>
                          <input type="password"  value={password} onChange={e => setPassword(e.target.value)} />
                        </div>

                        <div >
                          <label for="mobile">Mobile:</label>
                          <input type="text"  value={mobile} onChange={e => setMobile(e.target.value)} />
                        </div>
                        <br/>

                        <div >
                          <label for="gender">Gender:</label>
                          Male <input type="radio" value="male" name="gender"
                    onChange={e => setGender(e.target.value)} />
                          &nbsp;&nbsp;
                          Female <input type="radio" value="female" name="gender"
                    onChange={e => setGender(e.target.value)} />
                          &nbsp;&nbsp;
                        </div>
                        <br/>
                        <button type="button" onClick={handleSubmit} >Submit</button>
                    </form>
                </div>
            </div>
        </div>
      </div>
    </div>


  );
              }

export default Register;
