import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

const Display = () => {
  const myNav = useNavigate();
  const [taskData, setTaskData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = taskData.slice(firstIndex, lastIndex);
  const npage = Math.ceil(taskData.length / recordsPerPage);
  const numbers = [...Array(npage + 1).keys()].slice(1);

  // Load task data from the server
  const loadData = async () => {
    try {
      const response = await axios.get("http://localhost:8000/task/Display");
      setTaskData(response.data);
    } catch (error) {
      console.error("Error fetching task data:", error);
    }
  };

  // Handle toggling task status
  const handleToggleStatus = async (id, status) => {
    try {
      const newStatus = status === 'pending' ? 'completed' : 'pending';
      const updatedTodo = await axios.put(`http://localhost:8000/task/Display/${id}`, { status: newStatus });
      setTaskData(taskData.map(todo => (todo._id === id ? updatedTodo.data : todo)));
    } catch (error) {
      console.error("Error toggling task status:", error);
    }
  };

  // Delete task
  const del = (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      axios.post("http://localhost:8000/task/Delete", { id: id })
        .then(() => {
          loadData();
        })
        .catch((error) => {
          console.error("Error deleting item:", error);
        });
    }
  };

  // Edit task
  const edit = (id) => {
    myNav("/edit/" + id);
  };

  // View task description
  const descriptionHere = (description, date, descriptiondetail) => {
    myNav(`/Des/${description}/${date}/${descriptiondetail}`);
  };

  // Load task data when component mounts
  useEffect(() => {
    loadData();
  }, []);

  // Generate table rows for task data
  const mydata = records.map((task, index) => (
    <tr key={index}>
      <td className="sr">{index + 1}</td>
      <td>{task.title}</td>
      <td className="" onClick={() => descriptionHere(task.description, task.date, task.descriptiondetail)}>{task.description}</td>
      <td>{task.date}</td>
      <td>
        <div style={{ display: "flex" }}>
          <div className="stat">{task.status}</div>
          <label className="material-checkbox">
            <input type="checkbox" onClick={() => handleToggleStatus(task._id, task.status)} />
            <span className="checkmark"></span>
          </label>
        </div>
      </td>
      <td className="edit1">
        <button onClick={() => edit(task._id)} className="edit-button">
          
        <svg class="edit-svgIcon" viewBox="0 0 512 512">
                  <path d="M410.3 231l11.3-11.3-33.9-33.9-62.1-62.1L291.7 89.8l-11.3 11.3-22.6 22.6L58.6 322.9c-10.4 10.4-18 23.3-22.2 37.4L1 480.7c-2.5 8.4-.2 17.5 6.1 23.7s15.3 8.5 23.7 6.1l120.3-35.4c14.1-4.2 27-11.8 37.4-22.2L387.7 253.7 410.3 231zM160 399.4l-9.1 22.7c-4 3.1-8.5 5.4-13.3 6.9L59.4 452l23-78.1c1.4-4.9 3.8-9.4 6.9-13.3l22.7-9.1v32c0 8.8 7.2 16 16 16h32zM362.7 18.7L348.3 33.2 325.7 55.8 314.3 67.1l33.9 33.9 62.1 62.1 33.9 33.9 11.3-11.3 22.6-22.6 14.5-14.5c25-25 25-65.5 0-90.5L453.3 18.7c-25-25-65.5-25-90.5 0zm-47.4 168l-144 144c-6.2 6.2-16.4 6.2-22.6 0s-6.2-16.4 0-22.6l144-144c6.2-6.2 16.4-6.2 22.6 0s6.2 16.4 0 22.6z"></path>
                </svg>
        </button>
      </td>
      <td className="edit1">
        <button onClick={() => del(task._id)} className="Btn">
        <div class="sign">
                <svg
                    viewBox="0 0 16 16"
                    class="bi bi-trash3-fill"
                    fill="currentColor"
                    height="18"
                    width="18"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5m-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5M4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5"
                    ></path>
                  </svg>
                </div>
        </button>
      </td>
    </tr>
  ));

  // Pagination functionality
  const prevPage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage !== npage) {
      setCurrentPage(currentPage + 1);
    }
  };

  const changeCPage = (id) => {
    setCurrentPage(id);
  };

  return (
    <>
      <div style={{ backgroundColor: "#2d2d2d",height:"700px" }}>
        <center><h1 style={{ color: "white", marginBottom: "40px" }}>Assigned Tasks</h1></center>
        <div className="sidebar">
          <table id="utables" className="tables">
            <thead className="tablehead">
              <tr>
                <th className="sr">Sr.</th>
                <th>Title</th>
                <th>Description</th>
                <th>Date</th>
                <th>Status</th>
                <th className="edit1">Edit</th>
                <th className="edit1">Delete</th>
              </tr>
            </thead>
            <tbody className="tbody">
              {mydata}
            </tbody>
          </table>

          <nav className="page">
            <ul className="pagination">
              <li className="page-item">
                <a href="#" className="page-link" onClick={prevPage}>Prev</a>
              </li>
              {numbers.map((n) => (
                <li key={n} className={`page-item ${currentPage === n ? 'active' : ''}`}>
                  <a href="#" className="page-link" onClick={() => changeCPage(n)}>{n}</a>
                </li>
              ))}
              <li className="page-item">
                <a href="#" className="page-link" onClick={nextPage}>Next</a>
              </li>
            </ul>
          </nav>
        </div>
      </div>
    </>
  )
}

export default Display;
