import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Display = () => {
  const mynav = useNavigate();
  const [empData, setEmpData] = useState([]);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    axios.get("http://localhost:8000/task/Display").then((res) => {
      setEmpData(res.data);
    });
  };

  const handleToggleStatus = async (id, status) => {
    try {
      const newStatus = status === 'pending' ? 'completed' : 'pending';
      const updatedTodo = await axios.put(`http://localhost:9000/task/Display/${id}`, { status: newStatus });
      setEmpData(empData.map(todo => (todo._id === id ? updatedTodo.data : todo)));
    } catch (error) {
      console.error(error);
    }
  };

  const Hi = (description, date, fulldescription) => {
    mynav(`/Des/${description}/${date}/${fulldescription}`);
  };

  const handleDragStart = (e, id) => {
    e.dataTransfer.setData("id", id);
  };

  const handleDrop = (e, targetIndex, targetPriority) => {
    const id = e.dataTransfer.getData("id");
    const draggedItem = empData.find(item => item._id === id);
    const updatedEmpData = empData.filter(item => item._id !== id);
    if (draggedItem.priority !== targetPriority) {
      draggedItem.priority = targetPriority;
    }
    updatedEmpData.splice(targetIndex, 0, draggedItem);
    setEmpData(updatedEmpData);
  };

  const allowDrop = (e) => {
    e.preventDefault();
  };

  const getColorByPriority = (priority) => {
    switch (priority) {
      case 'high':
        return 'yellow';
      case 'medium':
        return 'orange';
      case 'low':
        return 'lightgreen';
      default:
        return 'transparent';
    }
  };

  return (
    <>
      <h1>Display records</h1>
      <div style={{ display: "flex" }}>
        {["high", "medium", "low"].map((priority) => (
          <div
            key={priority}
            onDrop={(e) => handleDrop(e, empData.length, priority)}
            onDragOver={(e) => allowDrop(e)}
            style={{ marginLeft: "20px", border: "1px solid black", padding: "10px" }}
          >
            <h2>{priority.charAt(0).toUpperCase() + priority.slice(1)} Priority</h2>
            {empData.map((item, index) => {
              if (item.priority === priority) {
                return (
                  <div
                    key={item._id}
                    draggable
                    onDragStart={(e) => handleDragStart(e, item._id)}
                    onDrop={(e) => handleDrop(e, index, priority)}
                    onDragOver={(e) => allowDrop(e)}
                    style={{ backgroundColor: getColorByPriority(item.priority), margin: "5px", padding: "5px" }}
                  >
                    <div onClick={() => Hi(item.description, item.date, item.fulldescription)}>{item.title}</div>
                    <div>Status: {item.status}</div>
                    <button onClick={() => handleToggleStatus(item._id, item.status)}>Toggle Status</button>
                  </div>
                );
              }
              return null;
            })}
          </div>
        ))}
      </div>
    </>
  );
};

export default Display;
