import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import 'bootstrap/dist/css/bootstrap.min.css';

const Display = () => {
  const myNav = useNavigate();
  const [taskData, setTaskData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const recordsPerPage = 5;
  const lastIndex = currentPage * recordsPerPage;
  const firstIndex = lastIndex - recordsPerPage;
  const records = taskData.slice(firstIndex, lastIndex);
  const totalPages = Math.ceil(taskData.length / recordsPerPage);
  const pageNumbers = [...Array(totalPages + 1).keys()].slice(1);

  const loadData = () => {
    axios.get("http://localhost:8000/task/Display").then((res) => {
      setTaskData(res.data);
    });
  };

  useEffect(() => {
    loadData();
  }, []);

  const handleToggleStatus = async (id, status) => {
    try {
      const newStatus = status === 'pending' ? 'completed' : 'pending';
      const updatedTodo = await axios.put(`http://localhost:8000/task/Display/${id}`, { status: newStatus });
      setTaskData(taskData.map(todo => (todo._id === id ? updatedTodo.data : todo)));
    } catch (error) {
      console.error(error);
    }
  };

  const del = (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this item?");
    if (confirmDelete) {
      const url = "http://localhost:8000/task/Delete";
      axios.post(url, { id: id })
        .then((res) => {
          loadData();
        })
        .catch((error) => {
          console.error("Error deleting item:", error);
        });
    }
  };

  const edit = (id) => {
    myNav("/edit/" + id);
  };

  const viewDescription = (description, date, descriptiondetail) => {
    myNav(`/Des/${description}/${date}/${descriptiondetail}`);
  };

  const renderData = records.map((key, index) => {
    return (
      <tr key={index} className="trs">
        <td className="sr">{index + 1}</td>
        <td className="td1">{key.title}</td>
        <td className="tds" onClick={() => viewDescription(key.description, key.date, key.descriptiondetail)}>{key.description}</td>
        <td className="date">{key.date}</td>
        <td className="date">
          <div style={{ display: "flex" }}>
            <div className="stat">{key.status}</div>
            <label class="material-checkbox">
              <input onClick={() => handleToggleStatus(key._id, key.status)} type="checkbox" />
              <span class="checkmark"></span>
            </label>
          </div>
        </td>
        <td className="edit1">
          <button onClick={() => { edit(key._id) }} class="edit-button">
            <svg class="edit-svgIcon" viewBox="0 0 512 512">
              {/* Edit SVG Icon */}
            </svg>
          </button>
        </td>
        <td className="edit1">
          <button onClick={() => { del(key._id) }} class="Btn">
            <div class="sign">
              {/* Trash SVG Icon */}
            </div>
            <div class="text">Delete</div>
          </button>
        </td>
      </tr>
    );
  });

  const prevPage = () => {
    if (currentPage !== 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  const nextPage = () => {
    if (currentPage !== totalPages) {
      setCurrentPage(currentPage + 1);
    }
  };

  const changePage = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <>
      <div style={{ backgroundColor: "#2d2d2d", height: "700px" }}>
        <div className="sidebar">
          <center>
            <h1 style={{ color: "green", paddingTop: "10px", paddingBottom: "30px" }}>Assigned Tasks</h1>
          </center>
          <div>
            <table className="tables">
              <thead className="tablehead">
                <tr className="trs">
                  <th className="sr">Sr.</th>
                  <th>Title</th>
                  <th >Description</th>
                  <th>Date</th>
                  <th>Status</th>
                  <th className="edit1">Edit</th>
                  <th className="edit1">Delete</th>
                </tr>
              </thead>
              <tbody className="tbody">
                {renderData}
              </tbody>
            </table>
            <nav class="page">
              <ul className="pagination">
                <li className="page-item">
                  <a href="#" className="page-link" onClick={prevPage}>Prev</a>
                </li>
                {pageNumbers.map((number, index) => (
                  <li key={index} className={`page-item ${currentPage === number ? 'active' : ''}`}>
                    <a href="#" className="page-link" onClick={() => changePage(number)}>{number}</a>
                  </li>
                ))}
                <li className="page-item">
                  <a href="#" className="page-link" onClick={nextPage}>Next</a>
                </li>
              </ul>
            </nav>
          </div>
        </div>
        <div style={{ margin: "100px", padding: "20px" }}>
        </div>
      </div>
    </>
  );
};

export default Display;
