import { useState, useEffect } from "react";
import axios from "axios";

const Prioritylist = () => {
  const [taskData, setTaskData] = useState([]);

  const loadData = () => {
    axios.get("http://localhost:8000/task/Display").then((res) => {
      setTaskData(res.data);
    });
  };

  useEffect(() => {
    loadData();
  }, []);

  const [draggedItem, setDraggedItem] = useState(null);

  const handleDragStart = (event, task) => {
    setDraggedItem(task);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event, targetPriority) => {
  event.preventDefault();
  if (!draggedItem) return;

  const updatedTasks = [...taskData];
  const draggedItemIndex = taskData.findIndex((task) => task === draggedItem);

  // Remove dragged item from its original position
  updatedTasks.splice(draggedItemIndex, 1);

  // Find the index where the dragged item should be inserted in the new priority list
  let targetPriorityIndex = 0;
  updatedTasks.forEach((task, index) => {
    if (task.priority === targetPriority) {
      targetPriorityIndex = index;
    }
  });

  // Insert dragged item at the correct index within the new priority list
  updatedTasks.splice(targetPriorityIndex, 0, draggedItem);

  setTaskData(updatedTasks);
  setDraggedItem(null);
};

  

  return (
    <div className="priority">
      <div
        className="prioritybox"
        onDragOver={(event) => handleDragOver(event)}
        onDrop={(event) => handleDrop(event, "High")}
      >
        <h1>High Priority</h1>
        {taskData.map((task, index) => {
          if (task.priority === "High") {
            return (
              <div
                key={task.id}
                draggable
                onDragStart={(event) => handleDragStart(event, task)}
              >
                <span>{task.title}</span>
                <span>{task.description}</span>
              </div>
            );
          }
        })}
      </div>
      <div
        className="prioritybox"
        onDragOver={(event) => handleDragOver(event)}
        onDrop={(event) => handleDrop(event, "Medium")}
      >
        <h1>Medium Priority</h1>
        {taskData.map((task, index) => {
          if (task.priority === "Medium") {
            return (
              <div
                key={task.id}
                draggable
                onDragStart={(event) => handleDragStart(event, task)}
              >
                <span>{task.title}</span>
                <span>{task.description}</span>
              </div>
            );
          }
        })}
      </div>
      <div
        className="prioritybox"
        onDragOver={(event) => handleDragOver(event)}
        onDrop={(event) => handleDrop(event, "Low")}
      >
        <h1>Low Priority</h1>
        {taskData.map((task, index) => {
          if (task.priority === "Low") {
            return (
              <div
                key={task.id}
                draggable
                onDragStart={(event) => handleDragStart(event, task)}
              >
                <span>{task.title}</span>
                <span>{task.description}</span>
              </div>
            );
          }
        })}
      </div>
    </div>
  );
};

export default Prioritylist;
