import { Link, Outlet, useNavigate } from "react-router-dom";
import { useState, useEffect } from 'react';
import Login from "./Components/Login";
import Register from "./Components/Register";

const Layout = () => {
  
 
  const [Headercontent, setHeadercontent] = useState();
  const myNav = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("_id",);
    localStorage.removeItem("name");
    localStorage.removeItem("email");
    localStorage.removeItem("mobile");
    localStorage.removeItem("address");
    localStorage.removeItem("city");
    localStorage.removeItem("gender");
    localStorage.removeItem("role");
    localStorage.removeItem("info");
  
    myNav("/Home");
  }

  useEffect(() => {

    if (localStorage.getItem("role") === "user") {
      setHeadercontent(
      <div style={{backgroundColor:"#2d2d2d"}} >
      <div>
      <div style={{display:"flex",marginTop:"0px",marginLeft:"400px",backgroundColor:"#2d2d2d"}} >
        <div  >
      <Link to="display" className="nvbar" ><h4>Display Task</h4></Link>
                  </div>
                  
     
        <div >
          
                  <div>
      <Link to="home"className="nvbar1" onClick={handleLogout} ><h4>Logout</h4></Link>
      </div>
              </div> 
       
              </div>
              </div>

              </div>
    
      );
    } 
    
    
    else
    
    
    {
      setHeadercontent(
        <div className="nbar">
          <div className="bars">
            <div className="navi">
              <h6 className="logo">TMS<br />solutions</h6>
              <div className="bar">
                <h6 className="tab">Solutions</h6>
                <h6 className="tab">Pricing</h6>
                <h6 className="tab">Task</h6>
              </div>
              

              
            </div>
          </div>
        </div>
      );
    }
  });

  return (
    <>
      {Headercontent}
      <Outlet />
    </>
  );
}

export default Layout;
