import {Link,Outlet, useNavigate} from "react-router-dom";
import {useState, useEffect} from 'react';
import Footer from "./Components/Footer";
import Popup from "./Components/Popup";
import Login from "./Components/Login";
import Register from "./Components/Register";


const Layout=()=>{


 
    const [isOpen, setIsOpen] = useState(false);
  const[open,setOpen]=useState(false);


  const openPopup = () => {
    setIsOpen(true);
  };

    const closePopup = () => {
      setIsOpen(false);
    };
  


  const [Headercontent,setHeadercontent]= useState();
  const myNav=useNavigate();
   const handleLogout=()=>{
    localStorage.removeItem("token");
    localStorage.removeItem("_id",);
    localStorage.removeItem("name");
    localStorage.removeItem("email");
    localStorage.removeItem("mobile");
    localStorage.removeItem("address");
    localStorage.removeItem("city");
    localStorage.removeItem("gender");
    localStorage.removeItem("role");
    localStorage.removeItem("info");

    myNav("/login");
   }

  useEffect(()=>{  
    
    if(localStorage.getItem("role")=="admin"){
    setHeadercontent(
      <nav class="navbar navbar-expand-lg navbar-dark bg-dark ">
      <div class="Navset">
          <a class="navbar-brand" href="#">Admin Panel</a>
          <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
          </button>
          <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav">
          <li class="nav-item">
          <a class="nav-link" aria-current="page" href="#"><Link to="admin" className="Nlinks" >AdminHome</Link></a>
        </li>
          <li class="nav-item">
            <a class="nav-link" href="#"><Link to="insert" className="Nlinks" >Assign</Link></a>
          </li>
          <li class="nav-item">
            <a class="nav-link" href="#"><Link to="display" className="Nlinks" >View</Link></a>
          </li>


          <li class="nav-item">
            <a class="nav-link" href="#"><Link to="Priority" className="Nlinks" >Priority View</Link></a>
          </li>

          <li class="nav-item">
            <a class="nav-link" href="#"><Link to="manageuser" className="Nlinks" >Users Information</Link></a>
          </li>
              <li class="nav-item">
              <a class="nav-link" href="#"><Link to="login" onClick={handleLogout} className="Nlinks" >Logout</Link></a>
              </li>
          </ul>
          </div>
      </div>
      </nav>

    )
}else if(localStorage.getItem("role")=="user"){
    setHeadercontent(
      <nav class="navbar navbar-expand-lg navbar-dark bg-dark " >
      <div class="Navset">
      <h3>User Panel</h3>
        <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse" id="navbarNav">
          <ul class="navbar-nav">
          <li class="nav-item">
          
        </li>
            <li class="nav-item">
              <a class="nav-link" href="#"><Link to="display"className="Nlinks" >Display Task</Link></a>
            </li>
        
            <li class="nav-item">
              <a class="nav-link" href="#"><Link to="login" onClick={handleLogout} className="Nlinks" >Logout</Link></a>
            </li>
          </ul>
        </div>
      </div>
    </nav>


    )
}else{
    setHeadercontent(

  
  <div className="Nav_2">
   <div className="Nav_3">
    <h4>Task Management System</h4>
   </div>





   
    <div  className="NavMain" >
      
       
    <div>


    <Link onClick={openPopup}  to="register"  >Sign up</Link>
      

      {/* Conditional rendering for the popup */}
      {isOpen && (
        <div className="popup-container">
          <div className="popup-box">
            {/* Close button */}
            <span className="close-btn" onClick={closePopup}>&times;</span>
           
            {/* Popup content */}
            <h2>This is a Popup</h2>
        <Register/>
            <p>Hello! This is some sample popup content.</p>
          </div>
        </div>
      )}
    </div>

  <Link onClick={openPopup}  className="Nav_1" to="login"  >Login</Link>
     {/* Conditional rendering for the popup */}
     {isOpen && (
        <div className="popup-container">
          <div className="popup-box">
            {/* Close button */}
            <span className="close-btn" onClick={closePopup}>&times;</span>
           
            {/* Popup content */}
            <h2>This is a Popup</h2>
        <Login/>
            <p>Hello! This is some sample popup content.</p>
          </div>
        </div>
      )}
        
    </div>
  </div>
    )
}


})

return(
    <>
      
      {Headercontent}
        

            <Outlet/>

      

<div className="Footer">
            <Footer/>
            </div>




    </>
)

}

export default Layout;
