import { useParams,useNavigate } from "react-router-dom";
import img1  from "../Asset/Two.jpg"
const Description = () => {
  const {description,  date, descriptiondetail } = useParams();
    const mynav= useNavigate();

    const navigateback=()=>{
        mynav("/display")
    }
  return (
    <>
     <h3>Description Page</h3>

           












<div className="details">



 
 <img style={{width:"350px",hieght:"200px",marginLeft:"20px",marginTop:"20px",borderRadius:"10px"}} src={img1}/>
 
  <br/>
  <span className="detdate">Date: {date}</span> 
<span
>
    <h5 className="detde">Description title:</h5><p className="detdes">{description}</p></span>

    <h5 className="detdes">Full Description:</h5>
<p className="detdes"> {descriptiondetail}
</p>


<button className="detdates"  onClick={navigateback}>back</button>



</div>
    </>




  );
};

export default Description;
