import { useState,useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"
import 'bootstrap/dist/css/bootstrap.min.css';
const Display=()=>{

  const mynav=useNavigate()
  const[taskData,setTaskData]=useState([])



  const [currentPage,setCurrentPage]= useState(1);
  const recordsperPage=5;
  const lastIndex = currentPage * recordsperPage;
  const firstIndex= lastIndex - recordsperPage;
  const records= taskData.slice(firstIndex,lastIndex);
  const npage= Math.ceil(taskData.length / recordsperPage);

  const numbers= [...Array(npage + 1).keys()].slice(1);



  const prevPage=()=>{
    if(currentPage!== 1){
      setCurrentPage(currentPage-1);
    }
  }


  const nextPage=()=>{
    if(currentPage!== npage){
      setCurrentPage(currentPage+1);
    }
  }


  const changeCPage=(id)=>{
    setCurrentPage(id);
  }




  const loadData=()=>{
          axios.get("http://localhost:8000/task/Display").then((res)=>{
          setTaskData(res.data)
          })
  }

      useEffect(()=>{
          loadData()
      },[])

      

      const handleToggleStatus = async (id, status) => {
          try {
            const newStatus = status === 'pending' ? 'completed': 'pending';
            const updatedTodo = await axios.put(`http://localhost:8000/task/Display/${id}`, { status: newStatus });
            setTaskData(taskData.map(todo => (todo._id === id ? updatedTodo.data : todo)));
          } catch (error) {
            console.error(error);
          }
        };


        const del = (id) => {
        
          const confirmDelete = window.confirm("Are you sure you want to delete this item?");
      
          
          if (confirmDelete) {
              
              const url = "http://localhost:8000/task/Delete";
              
              
              axios.post(url, { id: id })
                  .then((res) => {
                      
                      loadData();
                  })
                  .catch((error) => {
                      console.error("Error deleting item:", error);
                  });
          }
      };
      
      
      
      const edit=(id)=>{
      
          mynav("/edit/"+id); 
      
      }    

        const descriptionHere = ( description,date, descriptiondetail ) => {
      mynav(`/Des/${description}/${date}/${descriptiondetail}`);
    };
      

  const mydata=records.map((key, index) => {

    return(
      <>
          
          
              
                    <tbody>
                      <tr className="trs">
                        
                        <td className="td1">  { key.title}</td>
                        <td  className="tds" onClick={()=>descriptionHere(key.description, key.date, key.descriptiondetail)}>{key.description}</td>
                        <td  className="date"> {key.date}</td>
                      
                        <td className="date"> 
                        <div style={{display:"flex"}} >
                        


                         <label class="container">
    <input type="checkbox"  onClick={() => handleToggleStatus(key._id, key.status)} />
    <div class="checkmark">
        <svg xmlns="http://www.w3.org/2000/svg" class="ionicon" viewBox="0 0 512 512">
            <title>Checkmark</title>
            <path fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="32" d="M416 128L192 384l-96-96"></path>
        </svg>
    </div>
</label>

<div className="st">
{key.status}
</div>
                         
                               
                          </div></td>
                      <td className="date"><button onClick={()=>{edit(key._id)}} ><i  style={{color: "#000000"}}></i></button></td>
                      <td className="date"><button onClick={()=>{del(key._id)}}><i  style={{color: "#000000"}}></i></button></td>
                
                  </tr>
                  
              </tbody>
            
      
    
      </>
    )
    
  })


      

      return(
          <>
          <center>
            <h1  >Assigned Tasks</h1> </center>
            <div  >
                <div>
                  
                        <table   class="tables ">
                            <thead class="tablehead" >
                                <tr className="trs">
                                <th>Title</th>
                                <th >description</th>
                                <th>date</th>
                                <th>status</th>
                                <th>Edit</th>
                                <th>Delete</th>
                                </tr>
                            </thead>
                      {mydata}   
              </table>
          

                      <nav>
                        <ul className="pagination">
                            <li className="page-item">
                                <a href="#" className="page-link" onClick={prevPage}>Prev</a>
                            </li>
                            {
                              numbers.map((n,i)=>(
                                <li className={`page-item ${currentPage===n ? 'active':''}`} key={i}>
                                    <a href="#" className="page-link" onClick={()=>{changeCPage(n)}} >{n}</a>
                                </li>
                              ))
                            }
                            <li className="page-item">
                                <a href="#" className="page-link" onClick={nextPage}>Next</a>
                            </li>
                        </ul>
                      </nav>
              </div>
            </div>
            
                  <div style={{margin:"100px",padding:"20px"}}> 
                          
                  </div>
            
           

  </> 
)}       

export default Display;
