import { useParams,useNavigate } from "react-router-dom";
const Description = () => {
  const {description,  date, descriptiondetail } = useParams();
    const mynav= useNavigate();

    const navigateback=()=>{
        mynav("/display")
    }
  return (
    <>
     <h3>Description Page</h3>

           












<div className="details">

  <span className="detdate">Date: {date}</span> 

 
 
  <img src=""/>
 
  <br/>
<span
>
    <h5 className="detdes">Description title:</h5><p className="detdesc">{description}</p></span>

    <h5 className="detdesc">Full Description:</h5>
<p className="detdesc"> {descriptiondetail}
</p>


<button className="detdates"  onClick={navigateback}>back</button>



</div>
    </>




  );
};

export default Description;
